mapper.select:
select * from specification where ( iscustom = 0
or shop_id = #{shopId}
)
and specification_type like concat('%',#{specificationType},'%')
order by specification_id

mapper.insert:
insert into specification(
specification_type,
specification_desc

    ,iscustom


    ,shop_id

) values(
#{specificationType},
#{specificationDesc}

    ,#{isCustom}


    ,#{shopId}

)

mapper.update:
update specification
set
specification_type=#{specificationType},
specification_desc=#{specificationDesc},
where
iscustom=#{isCustom}
and shop_id=#{shopId}
and specification_id=#{id}
